let lastScrollTop=0
const navbar=document.querySelector('.navbar')
let scrollTimeout
window.addEventListener('scroll',()=>{const currentScroll=window.scrollY||document.documentElement.scrollTop
if(currentScroll>0){navbar.classList.add('navbar-shadow')
clearTimeout(scrollTimeout)
scrollTimeout=setTimeout(()=>{if(window.scrollY>0){navbar.classList.add('hidden-navbar')}},2000)}else{navbar.classList.remove('navbar-shadow')
navbar.classList.remove('hidden-navbar')}
if(currentScroll<lastScrollTop){navbar.classList.remove('hidden-navbar')}
lastScrollTop=currentScroll<=0?0:currentScroll})